import math

number=float(input("enter number:"))

print("ceil number is :",math.ceil(number))
print("floor value is :",math.floor(number))
print("truncate value is:",math.trunc(number))
print("factorial value is:",math.factorial(5)) #always passing value not passing variable

print(f"reminder of 10 divided by 3:",math.fmod(10,3))